<script setup>
import { onBeforeMount, onMounted } from 'vue'
import { useRouter } from 'vue-router'

import SidebarLeft from '@/components/SidebarLeft.vue'
// import SidebarRight from '@/components/SidebarRight.vue'
import StatBars from '@/components/StatBars.vue'

import { useNavigationStore } from '@/store/index'
import { useAccountStore } from '@/store/account'
import { useChapterStore } from './store/chapter'
// import axios from 'axios'

const router = useRouter()

const chapterStore = useChapterStore()
const navigationStore = useNavigationStore()
const accountStore = useAccountStore()

const colors = [
  ['back', '#fff'],
  ['char', '#fff'],
  ['skill', '#fff'],
  ['quest', '#fff'],
  ['achie', '#fff'],
  ['pact', '#fff'],
  ['para', '#fff']
]

const show = {
  showCharacter: true,
  showSkills: true,
  showQuestLog: true,
  showAchievements: true,
  showPacts: true,
  showParagon: true,
  showMenus: true
}

onBeforeMount(() => {
  // axios.defaults.xsrfHeaderName = 'X-CSRFTOKEN'
  // axios.defaults.xsrfCookieName = 'csrftoken'
  // axios.defaults.withCredentials = true
  accountStore.checkToken()
})

function accountPage () {
  router.push(accountStore.isAuthenticated ? '/account' : '/login')
}

function changeButtonColor (icon) {
  for (let i = 0; i < colors.length; i++) {
    if (colors[i][0] === icon) {
      colors[i][1] = '#00B300'
    } else {
      colors[i][1] = '#fff'
    }
  }
}

function showNav () {
  navigationStore.showChapterNav = !navigationStore.showChapterNav
}

</script>

